import React from 'react';
import './App.css';
import Login from './Login'; 
import Home from './Home';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom'; // Import เพิ่มเติม

function App() {
  return (
    <Router>
      <div className="app">
        <header className="header">
          <h1>Home</h1>
          <Link to="/login" className="login-button">Login</Link> {/* ใช้ Link แทนที่ปุ่ม */}
        </header>
        <div className="main-content">
          <main className="content">
            <Switch>
              <Route exact path="/" component={Home} />
              <Route path="/login" component={Login} /> {/* เพิ่ม Route สำหรับหน้า Login */}
            </Switch>
          </main>
        </div>
        <footer className="footer">
          <p>CS369 Group Project</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
